<?php

namespace Buseta\BusesBundle\Entity\Repository;

use Buseta\BusesBundle\Form\Model\ModuloCombustibleFilterModel;
use Doctrine\ORM\EntityRepository;
use Doctrine\ORM\NoResultException;

/**
 * ModuloCombustibleRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ModuloCombustibleRepository extends EntityRepository
{
    public function filter(ModuloCombustibleFilterModel $filter = null)
    {
        $qb = $this->createQueryBuilder('mc');
        $query = $qb->where($qb->expr()->eq(true,true));

        if($filter) {

            if ($filter->getCombustible() !== null && $filter->getCombustible() !== '') {
                $query->andWhere($query->expr()->eq('mc.combustible', ':combustible'))
                    ->setParameter('combustible', $filter->getCombustible());
            }
            if ($filter->getAutobus() !== null && $filter->getAutobus() !== '') {
                $query->andWhere($query->expr()->eq('mc.autobus', ':autobus'))
                    ->setParameter('autobus', $filter->getAutobus());
            }
            if ($filter->getChofer() !== null && $filter->getChofer() !== '') {
                $query->andWhere($query->expr()->eq('mc.chofer', ':chofer'))
                    ->setParameter('chofer', $filter->getChofer());
            }
        }

        $query->orderBy('mc.id', 'ASC');

        try {
            return $query->getQuery();
        } catch (NoResultException $e) {
            return array();
        }
    }
}
